    return (
        <div className="subtotal">
            <CurrencyFormat
            renderText={(value) =>(
                <>
                <p>
                    Subtotal (0 items):<strong>0</strong> 
                    <strong>{`${value}`}</strong>
                </p>
                <small className="subtotal_gift">
                    <input tylpe="checkbox" />This Order contains a gift
                </small>
                </>
            )}
            decimalScal={2}
            value={0}
            displayType={"text"}
            thousanSeperator={true}
            prefix={"$"}
            />
        </div>
    )
}

export default Subtotal
