import React, {useState} from 'react'
import './Product.css'
import { useStateValue } from './StateProvider'


const ImageSlider = () => {

    let slides = [{image:RC1},{image:RC2},{image:RC3},{image:RC4},{image:RC5}]  

    const [current, setCurrent] = useState(0);
    const length = slides.length;

    const nextSlide = () => {
      setCurrent(current === length - 1 ? 0 : current + 1);
    };

    const prevSlide = () => {
      setCurrent(current === 0 ? length - 1 : current - 1);
    };

    if (!Array.isArray(slides) || slides.length <= 0) {
      return null;
    }

    return (

          <section className='slider'>

            <button className='left-arrow' onClick={prevSlide}>
              <ArrowBackIcon className="arrow_icon"/>
            </button>       

            {slides.map((slide, index) => {
            return (
              <div
                className={index === current ? 'slide active' : 'slide'}
                key={index}
              >
                {index === current && (
                  <img src={slide.image} alt=' ' className='image' />
                )}
              </div>
            );
            })}

            <button className='right-arrow' onClick={nextSlide}>
              <ArrowForwardIcon className="arrow_icon"/>
            </button>

        </section>

    )
}


function Product({id, title, image, description, price}) {

        const [state, dispatch] = useStateValue();

        const addToBasket = () => {
            dispatch({
                type: 'ADD_TO_BASKET',
                item: {
                    id:id,
                    title: title,
                    image: image,
                    price: price,
                }
            })
        }

    return (
        <div className="product">
            <title>{title}</title>
            <div className="slider-wrapper">
                    <img className="slider_img" src={image[1]} alt=""/>
            </div>
            <div className= "Product_info">
                <p className= {description}>This is a carpet</p>
            <p className= "product_price">
                <small>$</small>
                <p>{price}</p>
            </p>
                <button className= "basketbtn" onClick={addToBasket}>Add to Basket</button>
            </div>
        </div>
    )
}

export default Product;
